import React, { useState, useEffect } from 'react';
import { Table, Form, FormControl, InputGroup } from 'react-bootstrap';
import axios from 'axios';

const App = () => {
  const [heroes, setHeroes] = useState([]);
  const [filteredHeroes, setFilteredHeroes] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [searchBy, setSearchBy] = useState('name');
  const [sortBy, setSortBy] = useState('ascension_year');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios.get('https://indonesia-public-static-api.vercel.app/api/heroes')
      .then(res => {
        setHeroes(res.data);
        setFilteredHeroes(res.data);
      })
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    setFilteredHeroes(
      heroes
        .filter(hero => {
          return hero[searchBy].toLowerCase().includes(searchKeyword.toLowerCase());
        })
        .sort((a, b) => {
          if (sortOrder === 'asc') {
            return a[sortBy] > b[sortBy] ? 1 : -1;
          } else {
            return a[sortBy] < b[sortBy] ? 1 : -1;
          }
        })
    );
  }, [heroes, searchKeyword, searchBy, sortBy, sortOrder]);

  const handleSearchKeywordChange = e => setSearchKeyword(e.target.value);
  const handleSearchByChange = value => setSearchBy(value);
  const handleSortByChange = value => {
    setSortBy(value);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  return (
    <div className="container my-5">
      <Form className="mb-3">
        <InputGroup>
          <FormControl
            type="text"
            placeholder="Search Hero"
            value={searchKeyword}
            onChange={handleSearchKeywordChange}
          />
          <InputGroup.Append>
            <Form.Control
              as="select"
              value={searchBy}
              onChange={e => handleSearchByChange(e.target.value)}
            >
              <option value="name">Name</option>
              <option value="description">Description</option>
            </Form.Control>
          </InputGroup.Append>
        </InputGroup>
      </Form>
      <Table responsive bordered hover>
        <thead>
          <tr className="bg-primary text-white">
            <th>
              Name
              <i className={`fa fa-sort-${sortBy === 'name' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('name')}></i>
              </th>
            <th>
              Description
              <i className={`fa fa-sort-${sortBy === 'description' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('description')}></i>
            </th>
            <th>
              Birth Year
              <i className={`fa fa-sort-${sortBy === 'birth_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('birth_year')}></i>
            </th>
            <th>
              Death Year
              <i className={`fa fa-sort-${sortBy === 'death_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('death_year')}></i>
            </th>
            <th>
              Ascension Year
              <i className={`fa fa-sort-${sortBy === 'ascension_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('ascension_year')}></i>
            </th>
          </tr>
        </thead>
        <tbody>
          {filteredHeroes.map(hero => (
            <tr key={hero.id}>
              <td>{hero.name}</td>
              <td>{hero.description}</td>
              <td>{hero.birth_year}</td>
              <td>{hero.death_year}</td>
              <td>{hero.ascension_year}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default App;
