import React, { useState, useEffect } from 'react';
import { Table, InputGroup, FormControl, DropdownButton, Dropdown } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortAsc, faSortDesc } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';

function App() {
  const [heroes, setHeroes] = useState([]);
  const [filteredHeroes, setFilteredHeroes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('name');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios.get('https://indonesia-public-static-api.vercel.app/api/heroes')
      .then(response => {
        setHeroes(response.data);
        setFilteredHeroes(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    setFilteredHeroes(
      heroes.filter(hero => {
        if (searchBy === 'name') {
          return hero.name.toLowerCase().includes(searchTerm.toLowerCase());
        } else if (searchBy === 'death_year') {
          return hero.death_year === Number(searchTerm);
        } else if (searchBy === 'birth_year') {
          return hero.birth_year === Number(searchTerm);
        } else if (searchBy === 'description') {
          return hero.description.toLowerCase().includes(searchTerm.toLowerCase());
        } else if (searchBy === 'ascension_year') {
          return hero.ascension_year === Number(searchTerm);
        }
        return true;
      })
    );
  }, [searchTerm, searchBy, heroes]);

  useEffect(() => {
    setFilteredHeroes(
      filteredHeroes.sort((a, b) => {
        if (sortOrder === 'asc') {
          if (sortBy === 'name') {
            return a.name.localeCompare(b.name);
          } else if (sortBy === 'death_year') {
            return a.death_year - b.death_year;
          } else if (sortBy === 'birth_year') {
            return a.birth_year - b.birth_year;
          } else if (sortBy === 'ascension_year') {
            return a.ascension_year - b.ascension_year;
          }
        } else {
          if (sortBy === 'name') {
            return b.name.localeCompare(a.name);
          } else if (sortBy === 'death_year') {
            return b.death_year - a.death_year;
          } else if (sortBy === 'birth_year') {
            return b.birth_year - a.birth_year;
          } else if (sortBy === 'ascension_year') {
            return b.ascension_year - a.ascension_year;
          }
        }
        return 0;
      })
    );
  }, [sortBy, sortOrder, filteredHeroes]);

  const handleSearchTermChange = event => {
    setSearchTerm(event.target.value);
  };

  const handleSearchByChange = value => {
    setSearchBy(value);
  };

  const handleSortByChange = value => {
    setSortBy(value);
  };

  const handleSortOrderChange = value => {
    setSortOrder(value);
  };

  return (
    <div className="container p-5">
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Search"
          aria-label="Search"
          aria-describedby="basic-addon2"
          value={searchTerm}
          onChange={handleSearchTermChange}
        />
        <DropdownButton
          as={InputGroup.Append}
          variant="outline-secondary"
          title={searchBy === 'name' ? 'Name' : searchBy === 'death_year' ? 'Death Year' : searchBy === 'birth_year' ? 'Birth Year' : searchBy === 'ascension_year' ? 'Ascension Year' : 'Description'}
          id="input-group-dropdown-2"
        >
          <Dropdown.Item onClick={() => handleSearchByChange('name')}>Name</Dropdown.Item>
          <Dropdown.Item onClick={() => handleSearchByChange('birth_year')}>Death Year</Dropdown.Item>
          <Dropdown.Item onClick={() => handleSearchByChange('death_year')}>Birth Year</Dropdown.Item>
          <Dropdown.Item onClick={() => handleSearchByChange('ascension_year')}>Ascension Year</Dropdown.Item>
          <Dropdown.Item onClick={() => handleSearchByChange('description')}>Description</Dropdown.Item>
        </DropdownButton>
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>
              Name
              <i className={`fa fa-sort-${sortBy === 'name' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('name')}></i>
          </th>
          <th>
            Death Year
            <i className={`fa fa-sort-${sortBy === 'death_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('death_year')}></i>
        </th>
        <th>
          Birth Year
          <i className={`fa fa-sort-${sortBy === 'birth_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('birth_year')}></i>
      </th>
      <th>
        Ascension Year
        <i className={`fa fa-sort-${sortBy === 'ascension_year' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('ascension_year')}></i> </th>
        <th> Description <i className={`fa fa-sort-${sortBy === 'description' ? sortOrder : 'asc'} ml-2`} onClick={() => handleSortByChange('description')}></i>
      </th>
    </tr>
</thead >
    <tbody>
      {filteredHeroes.map(hero => (
        <tr key={hero.id}>
          <td>{hero.name}</td>
          <td>{hero.birth_year}</td>
          <td>{hero.death_year}</td>
          <td>{hero.ascension_year}</td>
          <td>{hero.description}</td>
        </tr>
      ))}
    </tbody>
</Table >
</div >
);
};

export default App;
