import React, { useState, useEffect } from 'react';
import { Table, Container, Form, InputGroup, Dropdown, DropdownButton, FormControl } from 'react-bootstrap';

function App() {
  const [heroes, setHeroes] = useState([]);
  const [filteredHeroes, setFilteredHeroes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('name');


  useEffect(() => {
    fetch('https://indonesia-public-static-api.vercel.app/api/heroes')
      .then(response => response.json())
      .then(data => setHeroes(data));
  }, []);

  useEffect(() => {
    setFilteredHeroes(
      heroes.filter(hero => {
        if (searchBy === 'name') {
          return hero.name.toLowerCase().includes(searchTerm.toLowerCase());
        } else if (searchBy === 'death_year') {
          return hero.death_year === Number(searchTerm);
        } else if (searchBy === 'birth_year') {
          return hero.birth_year === Number(searchTerm);
        } else if (searchBy === 'description') {
          return hero.description.toLowerCase().includes(searchTerm.toLowerCase());
        } else if (searchBy === 'ascension_year') {
          return hero.ascension_year === Number(searchTerm);
        }
        return true;
      })
    );
  }, [searchTerm, searchBy, heroes]);

  const handleSearch = event => {
    setSearchTerm(event.target.value);
  };

  return (
    <Container>
      <Form>
        <InputGroup className="mb-3">
          <DropdownButton
            as={InputGroup.Prepend}
            variant="outline-secondary"
            title={searchBy === 'name'
            ? 'Name'
            : searchBy === 'death_year'
            ? 'Death Year'
            : searchBy === 'birth_year'
            ? 'Birth Year'
            : searchBy === 'description'
            ? 'Description'
            : searchBy === 'ascension_year'
            ? 'Ascension Year'
            : 'Search by'
          }
            id="input-group-dropdown-1"
          >
            <Dropdown.Item onClick={() => setSearchBy('name')}>Name</Dropdown.Item>
            <Dropdown.Item onClick={() => setSearchBy('birth_year')}>Birth Year</Dropdown.Item>
            <Dropdown.Item onClick={() => setSearchBy('death_year')}>Death Year</Dropdown.Item>
            <Dropdown.Item onClick={() => setSearchBy('description')}>Description</Dropdown.Item>
            <Dropdown.Item onClick={() => setSearchBy('ascension_year')}>Ascension Year</Dropdown.Item>
          </DropdownButton>
          <FormControl onChange={handleSearch} value={searchTerm} placeholder="Search" />
        </InputGroup>
      </Form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Birth Year</th>
            <th>Death Year</th>
            <th>Description</th>
            <th>ascensionYear</th>
          </tr>
        </thead>
        <tbody>
          {filteredHeroes.map((hero, index) => (
            <tr key={hero.id}>
              <td>{index+1}</td>
              <td>{hero.name}</td>
              <td>{hero.birth_year}</td>
              <td>{hero.death_year}</td>
              <td>{hero.description}</td>
              <td>{hero.ascension_year}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
}

export default App;
